mapper.update:
UPDATE tbl_timestamp
SET ts = #{newTimestamp}
WHERE
    tbl_id = #{tableId}
  AND ts = #{oldTimestamp}

UPDATE instant
SET state = #{newInstant.state}, action = #{newInstant.action}

    , duration = #{duration}


    , start_ts = #{startTs}

WHERE
tbl_id = #{oldInstant.tableId}
AND action = #{oldInstant.action}
AND ts = #{oldInstant.ts}
AND state = #{oldInstant.state}

mapper.delete:
DELETE
FROM
    instant
WHERE
    tbl_id = #{tableId}
  AND ts = #{ts}

mapper.select:
SELECT
    ts, action, state
FROM
    instant
WHERE
    tbl_id = #{tableId}
  AND state IN

      #{state}

ORDER BY ts DESC

    LIMIT #{limit}

SELECT
    instant_id
FROM
    instant
WHERE
    tbl_id = #{tableId}
  AND action = #{action}
  AND ts = #{ts}
  AND state = #{state}

mapper.delete:
DELETE
FROM
    instant_meta_data
WHERE
    tbl_id = #{tableId}
  AND ts = #{ts}

DELETE
FROM
    instant_meta_data
WHERE
    tbl_id = #{tableId}
  AND ts = #{ts}
  AND state = #{state}

mapper.select:
SELECT
    data
FROM
    instant_meta_data
WHERE
    tbl_id = #{tableId}
  AND action = #{action}
  AND ts = #{ts}
  AND state = #{state}
