mapper.update:
UPDATE instance
SET status        = #{status},
    schedule_time = now(),
    run_times     = run_times + 1
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}
  and status = 0

UPDATE instance
SET status = #{status}
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}

UPDATE instance
SET dorado_job_id  = #{doradoJobId},
    application_id = #{applicationId}
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}

UPDATE instance
SET status = #{status}
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}
  and status = 1

UPDATE instance
SET status = #{status}
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}
  and status = 1

UPDATE instance
SET status = #{status}
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}

mapper.select:
SELECT 
FROM instance
WHERE db_name = #{dbName}
  and table_name = #{tableName}
  and instant = #{instant}

SELECT

FROM instance
WHERE status = 2
and run_times  <=  #{maxRetry}
and update_time > #{queryStartTime}
order by id

SELECT

FROM instance
WHERE status = #{status}
and run_times > #{maxRetry}
and update_time > #{queryStartTime}
order by id

SELECT

FROM instance
WHERE status = #{status}
and update_time > #{queryStartTime}
order by id
